const buttons=[...document.querySelectorAll(".language-button")],links=[...document.querySelectorAll(".navigation-button")];buttons.forEach((t=>{t.addEventListener("click",(()=>{document.querySelector(".language-button.active").classList.remove("active"),t.classList.add("active")}))})),links.forEach((t=>{t.addEventListener("click",(()=>{document.querySelector(".navigation-button.navigation_active").classList.remove("navigation_active"),t.classList.add("navigation_active")}))}));const anchors=document.querySelectorAll('a[href*="#"]');for(let t of anchors)t.addEventListener("click",(function(e){e.preventDefault();const n=t.getAttribute("href").substr(1);document.getElementById(n).scrollIntoView({behavior:"smooth",block:"start"})}));